  gui.add(ui, "len", 20, 40).onChange(updateUi);
  gui.add(ui, "minLen", 16, 32).onChange(updateUi);
}

function updateUi() {
  ui.minLen = map(ui.len, 20, 40, 16, 32);
  gui.updateDisplay();
  redraw();
