  handPose.detectStart(video, gotHands);
}

function draw() {
  // 绘制摄像头视频
  image(video, 0, 0, width, height);

  // 当没有检测到手时停止所有音符（避免每帧发送 NoteOff）
  if (hands.length === 0) {
    if (noteplaying1) {
      stopNoteSafe(60, 1);
      noteplaying1 = false;
    }
    if (noteplaying2) {
      stopNoteSafe(65, 2);
      noteplaying2 = false;
    }
    return; // 直接返回
  }

  // 计算大拇指之间的距离
  if (hands.length >= 2) {
    let leftHand = hands[0];
    let rightHand = hands[1];

    // 获取大拇指位置
    let leftThumb = leftHand.thumb_tip;
    let rightThumb = rightHand.thumb_tip;

    // 检查 thumb_tip 是否存在
    if (leftThumb && rightThumb) {
      // console.log("左大拇指位置: ", leftThumb);
      // console.log("右大拇指位置: ", rightThumb);

      // 计算大拇指之间的距离
      let distance = dist(
        leftThumb.x,
        leftThumb.y,
        rightThumb.x,
        rightThumb.y
      );

      // console.log("大拇指之间的距离: ", distance);

      // 使用大拇指之间的距离控制音量
      volumeControl = constrain(map(distance, 0, 500, 1, 0), 0, 1);
      Tone.Master.volume.value = volumeControl * 100; // 将音量范围设置为 -100 到 0 分贝
      console.log("音量: ", volumeControl);
    } else {
      console.log("左大拇指或右大拇指的位置不存在");
    }
  }

  // 遍历所有检测到的手
  for (let i = 0; i < hands.length; i++) {
    let hand = hands[i];

    // 大拇指和食指的距离用于控制音符 60（频道 1）
    if (
      dist(
        hand.thumb_tip.x,
        hand.thumb_tip.y,
        hand.index_finger_tip.x,
        hand.index_finger_tip.y
      ) > 50
    ) {
      if (!noteplaying1) {
        playNoteSafe(60, 1); // 指定频道 1
        noteplaying1 = true;
      }
    } else {
      if (noteplaying1) {
        stopNoteSafe(60, 1); // 指定频道 1
        noteplaying1 = false;
      }
    }

    // 大拇指和中指的距离用于控制音符 65（频道 2）
    if (
      dist(
        hand.thumb_tip.x,
        hand.thumb_tip.y,
        hand.middle_finger_tip.x,
        hand.middle_finger_tip.y
      ) > 50
    ) {
      if (!noteplaying2) {
        playNoteSafe(65, 2); // 指定频道 2
        noteplaying2 = true;
      }
    } else {
      if (noteplaying2) {
        stopNoteSafe(65, 2); // 指定频道 2
        noteplaying2 = false;
      }
    }
  }
}

