    beginShape();
    curveVertex(0, 0);
    curveVertex(0, 0);
    curveVertex(3, -20);
    curveVertex(sin(frameCount * 0.05) * 2, -40);
    curveVertex(10, -20);
    curveVertex(0, 0);
    curveVertex(0, 0);
    endShape();
    pop();
  }
}

class Peaches {
  constructor(from, to, pct, size) {
    this.from = from;
    this.to = to;
    this.pct = pct * random(0.2, 0.8);
    this.size = size * random(0.8, 1);
    this.angle = random(PI * 2);

    this.col1 = color(
      250 + random(-30, 30),
      150 + random(-10, 10),
      140 + random(-10, 10),
      170
    );

    this.col2 = color(
      255 + random(-30, 30),
      280 + random(-10, 10),
      200 + random(-10, 10),
      70
    );
  }
  display() {
    let Px = lerp(this.from.x, this.to.x, this.pct);
    let Py = lerp(this.from.y, this.to.y, this.pct);

    push();
    translate(Px, Py + this.size);
    rotate(PI);
    noStroke();
    fill(this.col1);
    beginShape();
    curveVertex(sin(frameCount * 0.05) * 2, 0);
    curveVertex(sin(frameCount * 0.05) * 2, 0);
    curveVertex(-this.size / 3, this.size / 2);
    curveVertex(0, this.size * 0.8);
    curveVertex(this.size / 3, this.size / 2);
    curveVertex(sin(frameCount * 0.05) * 2, 0);
    curveVertex(sin(frameCount * 0.05) * 2, 0);
    endShape();
    rotate(PI / 10);
    beginShape();
    curveVertex(sin(frameCount * 0.05) * 2, 0);
    curveVertex(sin(frameCount * 0.05) * 2, 0);
    curveVertex(-this.size / 3, this.size / 2);
    curveVertex(0, this.size * 0.8);
    curveVertex(this.size / 3, this.size / 2);
    curveVertex(sin(frameCount * 0.05) * 2, 0);
    curveVertex(sin(frameCount * 0.05) * 2, 0);
    endShape();
    pop();

    push();
    translate(Px, Py + this.size);
    rotate(PI);
    noStroke();
    fill(this.col2);
    beginShape();
    curveVertex(sin(frameCount * 0.05) * 2, 0);
    curveVertex(sin(frameCount * 0.05) * 2, 0);
    curveVertex(-this.size / 3, this.size / 2);
    curveVertex(0, this.size * 0.8);
    curveVertex(this.size / 3, this.size / 2);
    curveVertex(sin(frameCount * 0.05) * 2, 0);
    curveVertex(sin(frameCount * 0.05) * 2, 0);
    endShape();
    pop();
  }
}

class Waterfall {
  constructor(x, y) {
    this.pos = createVector(x, y);
    this.vel = createVector(0, 0);
    this.acc = createVector();
    this.rad = random(1, 20);
    this.mass = this.rad;
  }

  applyForce(f) {
    let force = f.copy();
    force.div(this.mass);
    this.acc.add(force);
  }

  update() {
    this.vel.add(this.acc);
    this.pos.add(this.vel);
    this.acc.mult(0);
  }

  display() {
    push();
    translate(this.pos.x, this.pos.y);
    noStroke();
    fill(255);
    ellipse(0, 0, this.rad * 0.1, this.rad * 30);
    pop();
  }
}

