    if (random() < 0.6) {
      let angle1 = angle + BRANCH_ANGLE * 0.8 + random(-0.5, 0.5);
      branch(to, angle1, len, gen);
    }

    if (random() < 0.5) {
      let angle1 = angle + 0.5 * BRANCH_ANGLE + random(-0.5, 0.5);
