class Malus {
  constructor(from, to, pct) {
    this.from = from;
    this.to = to;
    this.pct = pct * random(0.2, 0.8);
    this.angle = random(PI * 2);

    this.col = color(
      280 + random(-30, 30),
      200 + random(-10, 10),
      200 + random(-10, 10),
      180
    );
  }
  display() {
    let drawShape = () => {
      beginShape();
      curveVertex(0, 0);
      curveVertex(0, 0);
      curveVertex(-7, -18);
      curveVertex(sin(frameCount * 0.05) * 4, -27);
      curveVertex(7, -18);
      curveVertex(0, 0);
      curveVertex(0, 0);
      endShape();
    };

    let drawShapes = () => {
      for (let i = 0; i < 5; i++) {
        rotate(PI / 2.5);
        drawShape();
      }
      rotate(PI / 6);
      drawShape();
    };

    let Px = lerp(this.from.x, this.to.x, this.pct);
    let Py = lerp(this.from.y, this.to.y, this.pct);

    push();
    translate(Px, Py);
    rotate(this.angle);
    scale(0.6);
    noStroke();
    fill(this.col);
    drawShapes();
    pop();

    push();
    translate(Px, Py);
    noStroke();
    fill(255);
    circle(0, 0, 5);
    pop();

    push();
    translate(Px, Py);
    rotate(this.angle);
    noStroke();
    fill(255, 255, 200);
    circle(sin(frameCount * 0.05) * 4, 40, 5);
    pop();

    push();
    translate(Px, Py);
    rotate(this.angle);
    scale(0.5);
    stroke(255);
    noFill();
    beginShape();
    curveVertex(sin(frameCount * 0.05) * 4, 20);
    curveVertex(sin(frameCount * 0.05) * 4, 20);
    curveVertex(8, 10);
    curveVertex(0, 0);
    curveVertex(0, 0);
    endShape();
    beginShape();
    curveVertex(-sin(frameCount * 0.05) * 4, 20);
    curveVertex(-sin(frameCount * 0.05) * 4, 20);
    curveVertex(-8, 10);
    curveVertex(0, 0);
    curveVertex(0, 0);
    endShape();
    pop();
  }
}

