        desiredVecFrom.mult(-1); // *** flip this to avoid!

        let steerForceFrom = p5.Vector.sub(desiredVecFrom, this.vel);
        steerForceFrom.limit(this.maxSteerForce);

        this.applyForce(steerForceFrom);
      }

      if (distanceTo < this.senseRad + 50) {
        desiredVecTo.normalize();
        desiredVecTo.mult(this.maxSpeed);
        desiredVecTo.mult(-1); // *** flip this to avoid!

        let steerForceTo = p5.Vector.sub(desiredVecTo, this.vel);
        steerForceTo.limit(this.maxSteerForce);

        this.applyForce(steerForceTo);
      }
    }
  }
  reappear() {
    if (this.pos.x < 0) {
      this.pos.x = 0;
    } else if (this.pos.x > width) {
      this.pos.x = width;
    }
    if (this.pos.y < 0) {
      this.pos.y = 0;
    } else if (this.pos.y > height) {
      this.pos.y = height;
    }
  }
  update() {
    this.vel.add(this.acc);
    this.pos.add(this.vel);
    this.acc.mult(0);
    this.angle = this.vel.heading();
  }
  applyForce(f) {
    if (this.mass <= 0) {
      console.log("Wrong mass");
      return;
    }
    let force = f.copy();
    force.div(this.mass);
    this.acc.add(force);
  }
  display() {
    push();
    translate(this.pos.x, this.pos.y);
    if (mouseX >= this.pos.x) {
      rotate(this.angle);
      rotate(-PI / 1.6);
      noStroke();
      fill(0, 0, 20);
      beginShape();
      curveVertex(0, 0);
      curveVertex(0, 0);
      curveVertex(-8, -20);
      curveVertex(sin(frameCount * 0.05) * 4, -30);
      curveVertex(8, -20);
      curveVertex(0, 0);
      curveVertex(0, 0);
      endShape();
      rotate(PI / 1.6);
      rotate(-PI / 6);
      beginShape();
      curveVertex(0, 0);
      curveVertex(0, 0);
      curveVertex(-8, -20);
      curveVertex(sin(frameCount * 0.05) * 4, -30);
      curveVertex(8, -20);
      curveVertex(0, 0);
      curveVertex(0, 0);
      endShape();
      rotate(-PI / 3);
      beginShape();
      curveVertex(0, 0);
      curveVertex(0, 0);
      curveVertex(-8, -20);
      curveVertex(sin(frameCount * 0.05) * 4, -30);
      curveVertex(8, -20);
      curveVertex(0, 0);
      curveVertex(0, 0);
      endShape();
      rotate(PI / 6);
      beginShape();
      curveVertex(0, 0);
      curveVertex(0, 0);
      curveVertex(-8, -20);
      curveVertex(sin(frameCount * 0.05) * 4, -40);
      curveVertex(8, -20);
      curveVertex(0, 0);
      curveVertex(0, 0);
      endShape();
    } else {
      rotate(-this.angle);
      rotate(PI / 1.6);
      noStroke();
      fill(0, 0, 20);
      beginShape();
      curveVertex(0, 0);
      curveVertex(0, 0);
      curveVertex(-8, 20);
      curveVertex(sin(frameCount * 0.05) * 4, 30);
      curveVertex(8, 20);
      curveVertex(0, 0);
      curveVertex(0, 0);
      endShape();
      rotate(-PI / 1.6);
      rotate(PI / 6);
      beginShape();
      curveVertex(0, 0);
      curveVertex(0, 0);
      curveVertex(-8, 20);
      curveVertex(sin(frameCount * 0.05) * 4, 30);
      curveVertex(8, 20);
      curveVertex(0, 0);
      curveVertex(0, 0);
      endShape();
      rotate(PI / 3);
      beginShape();
      curveVertex(0, 0);
      curveVertex(0, 0);
      curveVertex(-8, 20);
      curveVertex(sin(frameCount * 0.05) * 4, 30);
      curveVertex(8, 20);
      curveVertex(0, 0);
      curveVertex(0, 0);
      endShape();
      rotate(-PI / 6);
      beginShape();
      curveVertex(0, 0);
      curveVertex(0, 0);
      curveVertex(-8, 20);
      curveVertex(sin(frameCount * 0.05) * 4, 40);
      curveVertex(8, 20);
      curveVertex(0, 0);
      curveVertex(0, 0);
      endShape();
    }
    pop();
  }
}

class Rain {
  constructor(x, y) {
    this.pos = createVector(x, y);
    this.vel = createVector(0, 0);
    this.acc = createVector();
    this.rad = random(1, 20);
    this.mass = this.rad;
    this.angle = 0;
  }

  applyForce(f) {
    let force = f.copy();
    force.div(this.mass);
    this.acc.add(force);
  }

  update() {
    this.vel.add(this.acc);
    this.pos.add(this.vel);
    this.acc.mult(0);
    this.angle = this.vel.heading();
  }

  display() {
    push();
    translate(this.pos.x, this.pos.y);
    noStroke();

    fill(255, random(90, 100));
    rotate(this.angle);
    ellipse(0, 0, this.rad * 4, this.rad * 0.2);

    pop();
  }
}

