    curveVertex(this.size / 3, (-this.size * 1.5) / 2);
    curveVertex(sin(frameCount * 0.05), -this.size * 1.5);
    curveVertex(sin(frameCount * 0.05), -this.size * 1.5);
    endShape();
    rotate(PI / 3);
    fill(this.col1);
    beginShape();
    curveVertex(sin(frameCount * 0.05), -this.size * 1.3);
