function drawScene4() {
  if (a <= 255) {
    a++;
  } else if (a > 255) {
    a = 255;
  }
  bowl.display();

  if (a >= 255) {
    b++;
    push();
    translate(355, 350);
    fill(255, b);
    textFont("Long Cang");
    text("难缠再难，", 0, 0);
    text("终成Soup配饭。", 0, 35);
    pop();
  }

  push();
  translate(width / 2, height / 2);
  scale(1.7, 0.9);
  translate(-width / 2, -height / 2);
  for (let i = 0; i < waterbubbles.length; i++) {
    let w = waterbubbles[i];
    let anglewb = map(sin(frameCount), -1, 1, 80, 90);
    let centerPos = createVector(width / 2, height / 2);
    let vector = p5.Vector.sub(centerPos, w.pos);
    vector.normalize();
    vector.mult(0.1);
    vector.rotate(radians(anglewb)); // sin()
    w.applyForce(vector);

    w.vel.mult(0.9); // slow down a bit
    w.checkBoundaries();
    w.update();
    if (a >= 255) {
      w.display();
    }
  }
  pop();

  let vector = p5.Vector.sub(egg.centerPos, egg.pos);
  vector.normalize();
  vector.mult(0.1);
  vector.rotate(radians(egg.angle));
  egg.applyForce(vector);
  egg.angleMovement();
  egg.vel.mult(0.8);
  egg.checkBoundaries();
  egg.update();
  egg.display();

  for (let i = 0; i < swinsoups.length; i++) {
    let p = swinsoups[i];

    p.angleMovement();

    let vector = p5.Vector.sub(p.centerPos, p.pos);
    vector.normalize();
    vector.mult(0.15);
    vector.rotate(radians(p.angle));
    p.applyForce(vector);

    p.vel.mult(0.8);
    p.checkBoundaries();
    p.update();
    p.display();
    p.drag();
  }
  chopsticks.updatePosition(mouseX, mouseY);
  chopsticks.display();
  for (let i = 0; i < hotgas.length; i++) {
    let gs = hotgas[i];
    if (a >= 255) {
      gs.display();
    }
  }

  push();
  noStroke();
  fill(255, 100 - a * 0.4);
  rect(0, 0, width, height);
  pop();
}

