    this.rad = rad;
    this.mass = rad * 0.5;
    this.damping = 0.95;
    this.isImmovable = immovable;
  }
  update() {
    if (this.isImmovable) return;
    this.vel.add(this.acc);
    this.pos.add(this.vel);
    this.acc.mult(0);
    this.vel.mult(this.damping);
  }
  applyForce(f) {
    if (this.mass > 0) {
