class Hotgas {
  constructor(x, y, rad) {
    this.pos = createVector(x, y);
    this.rad = rad;
  }
  display() {
    push();
    translate(width / 2, height / 2 - 65);
    translate(this.pos.x, this.pos.y);
    scale(1.5);
    noStroke();
    fill(255, 50 + 50 * sin(this.pos.y * frameCount * 0.01));
    circle(0, 0, this.rad * 2);
    pop();

    push();
    translate(width / 2 - 50, height / 2 - 55);
    translate(this.pos.x, this.pos.y);
    noStroke();
    fill(255, 50 + 50 * sin(this.pos.y * frameCount * 0.01));
    circle(0, 0, this.rad * 2);
    pop();

    push();
    translate(width / 2 + 50, height / 2 - 55);
    translate(this.pos.x, this.pos.y);
    noStroke();
    fill(255, 50 + 50 * sin(this.pos.y * frameCount * 0.01));
    circle(0, 0, this.rad * 2);
    pop();
  }
}

