  if (keyIsPressed && key === "1") {
    b1Visible = true;
    b2Visible = false;
    b3Visible = false;
    b4Visible = false;
    b1lVisible = false;
    tearBounce = false;
    z = 0;
    eye1 = new Eye(200, 70);
    eye2 = new Eye(500, 70);
  }

  if (b1Visible == true) {
    eye1.display();
    eye2.display();
    if (keyIsPressed && key === "z") {
      if (z < 60) {
        z = z + 1;
      }
      if (z >= 60) {
        z = 0;
      }
    }
    if (z == 50) {
      for (let i = 0; i < 5; i++) {
        tears.push(new Tear(i * 45, 10 + 30 * sin((i / 5) * PI)));
