let eye2;
let z = 0;
let tears = [];
let lights1 = [];
let springs1 = [];
let C_GRAVITY = 1;
let DISTANCE_BTW_BALLS = 30;

//scene2
let boatInSea;
let rains = [];
let WATER_LEVEL = 150;
let shouldRaiseWaterLevel = false;
let lights2 = [];
let springs2 = [];
let seaWeeds = [];
let springs3_1 = [];
let springs3_2 = [];

//scene3
let boats = [];
let bounceCountBE = 0;

function setup() {
  createCanvas(700, 500);
  ll1 = new Light1(290, height, 15);
  lights1.push(ll1);
  lm1 = new Light1(350, height - 80, 30);
  lights1.push(lm1);
  lr1 = new Light1(410, height, 15);
