function draw() {
  background(0, 10);
  ll1.pos.x = 320;
  ll1.pos.y = height;
  lr1.pos.x = 380;
  lr1.pos.y = height;

  lm2.pos.x = 350;
  lm2.pos.y = 140;

  sw1.pos.x = 20;
  sw1.pos.y = height;
  sw7.pos.x = 30;
  sw7.pos.y = height;

  sw8.pos.x = 120;
  sw8.pos.y = height;
  sw14.pos.x = 130;
  sw14.pos.y = height;

  //Scene1
  if (keyIsPressed && key === "1") {
    b1Visible = true;
    b2Visible = false;
    b3Visible = false;
    b4Visible = false;
    b1lVisible = false;
    z = 0;
    eye1 = new Eye(200, 70);
    eye2 = new Eye(500, 70);
  }

  if (b1Visible == true) {
    eye1.display();
    eye2.display();
    if (keyIsPressed && key === "z") {
      if (z < 60) {
        z = z + 1;
      }
      if (z >= 60) {
        z = 0;
      }
    }
    if (z == 50) {
      for (let i = 0; i < 5; i++) {
        tears.push(new Tear(i * 45, 10 + 30 * sin((i / 5) * PI)));
        if (tears.length >= 10) {
          tears.shift();
        }
        b1lVisible = true;
      }
    } else if (lm1.pos.y <= 330) {
      b1lVisible = false;
    }

    if (b1lVisible == true) {
      for (let s1 of springs1) {
        s1.update();
        s1.display();
      }

      for (let l1 of lights1) {
        l1.drag();
        l1.update();
        l1.display();
      }
      push();
      strokeWeight(random(1, 5));
      stroke(255);
      noFill();
      //fill(220, 90);
      beginShape();
