class Rain {
  constructor(x, y) {
    this.pos = createVector(x, y);
    this.vel = createVector(0, 0);
    this.acc = createVector();
    this.rad = random(1, 20);
    this.mass = this.rad;
  }

  applyForce(force) {
    let f = force.copy();
    f.div(this.mass);
    this.acc.add(f);
  }

  update() {
    this.vel.add(this.acc);
    this.pos.add(this.vel);
    this.acc.mult(0);
  }

  bounce() {
    if (this.pos.y > WATER_LEVEL + 20) {
      this.pos.y = WATER_LEVEL + 20;
      this.vel.y *= -1;
    }
  }

  display(WATER_LEVEL) {
    push();
    translate(110, 0);
    translate(this.pos.x, this.pos.y);
    noStroke();
    if (this.pos.y < WATER_LEVEL) {
      // rain
      fill(255, 10);
      circle(0, 0, this.rad);
    } else {
      // waterbubble
      fill(255, 10);
      ellipse(0, 0, this.rad * 3, this.rad * 0.5);
    }
    pop();

    push();
    translate(410, 0);
    translate(this.pos.x, this.pos.y);
    noStroke();
    if (this.pos.y < WATER_LEVEL) {
      // rain
      fill(255, 10);
      circle(0, 0, this.rad);
    } else {
      // waterbubble
      fill(255, 10);
      ellipse(0, 0, this.rad * 3, this.rad * 0.3);
    }
    pop();
  }
}

