  for (let i = 0; i < boats.length; i++) {
    let boat = boats[i];
    boat.applyGravitationalAttraction(boats);
    boat.update();
    boat.limitVelocity(5);
    boat.checkCollision();
    boat.bounce();
    boat.display();
  }
  // move to the next scene
  if (bounceCountBE >= 3000) {
    b1lVisible = false;

    scene = 4;
    bowl = new Bowl(350, 250);
  }
}

