//adapted from the example code given by Prof.Moon: //https://editor.p5js.org/MOQN/sketches/gtzqJ5kKZ
class Waterbubble {
  constructor(x, y, rad) {
    this.pos = createVector(x, y);
    this.vel = createVector(random(-2, 2), 0);
    this.acc = createVector(0, 0);
    this.rad = rad;
    this.mass = rad * 0.05;
  }
  applyForce(f) {
    if (this.mass > 0) {
      let force = p5.Vector.div(f, this.mass);
      this.acc.add(force);
    }
  }
  update() {
    this.vel.add(this.acc);
    this.pos.add(this.vel);
    this.acc.mult(0);
  }
  checkBoundaries() {
    // x
    if (this.pos.x < 20) {
      this.pos.x = 20;
      this.vel.x = -this.vel.x;
    } else if (this.pos.x > width - 20) {
      this.pos.x = width - 20;
      this.vel.x = -this.vel.x;
    }
    // y
    if (this.pos.y < 150) {
      this.pos.y = 150;
      this.vel.y = -this.vel.y;
    } else if (this.pos.y > height - 100) {
      this.pos.y = height - 200;
      this.vel.y = -this.vel.y;
    }
  }
  display() {
    push();
    translate(this.pos.x, this.pos.y);
    noStroke();
    fill(255, random(80, 90));
    circle(0, 0, this.rad * 2);
    pop();
  }
}

