    for (let i = 0; i < b3s.length; i++) {
      if (frameCount > startFrameCount + i * 2) {
        b3s[i].display();
      }
    }
    for (let j = 0; j < b3f.length; j++) {
      if (frameCount > startFrameCount + j * 0.8) {
