//fish
class SH {
  constructor(x, y) {
    do {
      this.x = mouseX + random(-40, 40);
      this.y = mouseY + random(-40, 40);
    } while (
      this.x > mouseX - 20 &&
      this.x < mouseX + 20 &&
      this.y > mouseY - 20 &&
      this.y < mouseY + 20
    );
    //
    this.vel = createVector(random(-0.2, 0.2), random(-0.2, 0.2));
    this.speed = random(1, 1.5);
    //
    this.a = random(0, 20);
    this.a = this.a++;
    //
    this.isDone = false;
  }
  bounce() {
    if (this.x < 0) {
      this.x = 0;
      this.vel.x = abs(this.vel.x);
    } else if (this.x > width) {
      this.x = width;
      this.vel.x = -abs(this.vel.x);
    }
    if (this.y < 0) {
      this.y = 0;
      this.vel.y = abs(this.vel.y);
    } else if (this.y > height) {
      this.y = height;
      this.vel.y = -abs(this.vel.y);
    }
  }
  move() {
    this.x += this.vel.x * this.speed;
    this.y += this.vel.y * this.speed;
    this.bounce();
  }
  speedUp() {
    this.speed *= 1.05;
  }
  display() {
    push();
    translate(this.x, this.y);
    rotate(this.vel.heading());
    noStroke();
    fill(255, 0, 0, this.a);
    scale(random(1, 2));
    ellipse(0, 0, 15, 6);
    triangle(-6, 0, -12, -3, -12, 3);
    pop();
  }
}

